import React from "react";
import { top100FilmsType } from "../../data";

import "./style.scss";
import { ComponentProps } from "./types";

const ControlledInput: React.FC<ComponentProps> = ({ data }) => {
  const searchItem = (filter: string) => (e: any) => {
    e.preventDefault();
  };

  const dataOptions = data.map((e: top100FilmsType) => {
    return <option value={e.title} key={Math.random() * Math.random()} />;
  });

  return (
    <form className="controlled-form">
      <input list="browsers" className="controlled-input" />
      <datalist id="browsers">{dataOptions}</datalist>
      <button className="controlled-button" onClick={() => searchItem}>
        Search
      </button>
    </form>
  );
};

export default ControlledInput;
