import React from "react";
import { top100FilmsType } from "../../data";

import "./style.scss";
import { ComponentProps } from "./types";

const UncontrolledInput: React.FC<ComponentProps> = ({ data, mode }) => {
  const input = React.useRef<HTMLInputElement>(null);

  const handleSubmit = (event: any) => {
    console.log("Имя: " + input?.current?.value);
    event.preventDefault();
  };

  let dataOptions;

  if (input?.current?.value) {
    dataOptions = data.map((e: top100FilmsType) => {
      return <option value={e.title} key={Math.random() * Math.random()} />;
    });
  }

  console.log(dataOptions);
  console.log(input?.current?.value);
  console.log(data);

  return (
    <>
      <form onSubmit={handleSubmit}>
        <input
          list="data"
          className="uncontrolled-input"
          type="text"
          ref={input}
          required
        />
        <datalist id="data">{dataOptions}</datalist>
        <button className="uncontrolled-button">Search</button>
      </form>
    </>
  );
};

export default UncontrolledInput;
