import React from "react";

import "./style.scss";
import { ComponentProps } from "./types";

const UncontrolledInput: React.FC<ComponentProps> = ({ data, mode }) => {
  const input = React.useRef<HTMLInputElement>(null);

  const handleSubmit = (event: any) => {
    console.log("Имя: " + input?.current?.value);
    event.preventDefault();
    console.log(input);
  };

  return (
    <>
      <input className="c-checkbox" type="checkbox" id="checkbox" />
      <div className="c-formContainer">
        <form className="c-form" onSubmit={handleSubmit}>
          <input className="c-form__input" type="text" ref={input} required />
          <label className="c-form__buttonLabel" htmlFor="checkbox">
            <button className="c-form__button" type="button">
              Search
            </button>
          </label>
          <label
            className="c-form__toggle"
            htmlFor="checkbox"
            data-title="Notify me"
          ></label>
        </form>
      </div>
    </>
  );
};

export default UncontrolledInput;
