import React from "react";

import "./styles.scss";
import { ComponentProps } from "./types";

const Timer: React.FC<ComponentProps> = ({
  settings,
  onComplete,
  updateTimer,
}) => {
  const { currentHours, currentMinutes, currentSeconds } = settings;
  return (
    <>
      <h1 className="timer">
        {currentHours > 9 ? currentHours : "0" + currentHours} :
        {currentMinutes > 9 ? currentMinutes : "0" + currentMinutes} :
        {currentSeconds > 9 ? currentSeconds : "0" + currentSeconds}
      </h1>
      <h2 className="timer">{new Date().toLocaleTimeString()}</h2>
      <>
        <button onClick={updateTimer}>Renew</button>
        <button onClick={onComplete}>Pause</button>
        <button onClick={onComplete}>Reset</button>
      </>
    </>
  );
};

export default Timer;
