import React, { useEffect, useRef, useState } from "react";

import { ComponentProps, Settings } from "./types";

import Layout from "../components/layout";
import Timer from "../components/timer";

const date = new Date();

const initialSettings: Settings = {
  currentHours: date.getHours(),
  currentMinutes: date.getMinutes(),
  currentSeconds: date.getSeconds(),
};

const TimerPage: React.FC<ComponentProps> = () => {
  const [hours, setHours] = useState(0);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);

  const [timer, setTimer] = useState(date);

  const settings: Settings = {
    currentHours: hours,
    currentMinutes: minutes,
    currentSeconds: seconds,
  };

  const { currentHours, currentMinutes, currentSeconds } = initialSettings;

  useEffect(() => {
    setHours(currentHours);
    setMinutes(currentMinutes);
    setSeconds(currentSeconds);
  }, []);

  function usePrevious(timer: number) {
    const prevTimer = useRef();
    useEffect(() => {
      prevTimer.current = timer;
    }, [timer]);
    return prevTimer.current;
  }

  const prevTimer = usePrevious(+timer);

  useEffect(() => {
    //Timers

    const tickTimer = setInterval(() => {
      setTimer(new Date(+prevTimer - 1));
    }, 1000);
    // const hoursTimer = setInterval(() => {
    //   setHours(hours - 1);
    // }, 3600000);

    // const minutesTimer = setInterval(() => {
    //   setMinutes(minutes - 1);
    // }, 60000);

    // const secondsTimer = setInterval(() => {
    //   setSeconds(seconds);
    // }, 1000);

    // const dateTimer = setInterval((date) => {
    //   // setSeconds(+prevDate - 1);
    // }, 1000);

    // //Condition to clear
    // if (!hours) clearInterval(hoursTimer);
    // if (!minutes) clearInterval(minutesTimer);
    // if (!seconds) clearInterval(secondsTimer);
  }, [timer]);

  const onTimerCompleted = (): void => {
    !hours && !minutes && !seconds && console.log("Boom!");
  };

  onTimerCompleted();

  const updateTimer = (): void => {
    setHours(currentHours);
    setMinutes(currentMinutes);
    setSeconds(currentSeconds);
  };

  const pauseTimer = (): void => {};

  const resetTimer = (): void => {
    setHours(0);
    setMinutes(0);
    setSeconds(0);
  };

  return (
    <Layout>
      <Timer
        settings={settings}
        updateTimer={updateTimer}
        onComplete={resetTimer}
      >
        {(hours: number, minutes: number, seconds: number) => (
          <>
            <span>{hours}</span>
            <span>{minutes}</span>
            <span>{seconds}</span>
          </>
        )}
      </Timer>
    </Layout>
  );
};

export default TimerPage;
