import React from "react";

import "./styles.scss";
import { ComponentProps } from "./types";

const Timer: React.FC<ComponentProps> = ({
  settings,
  onComplete,
  updateTimer,
  pauseTimer,
}) => {
  const hours = settings.getHours();
  const minutes = settings.getMinutes();
  const seconds = settings.getSeconds();

  return (
    <>
      {/* <h1 className="timer">
        {currentHours > 9 ? currentHours : "0" + currentHours} :
        {currentMinutes > 9 ? currentMinutes : "0" + currentMinutes} :
        {currentSeconds > 9 ? currentSeconds : "0" + currentSeconds}
      </h1> */}
      <h1 className="timer">
        {hours > 9 ? hours : "0" + hours} :
        {minutes > 9 ? minutes : "0" + minutes} :
        {seconds > 9 ? seconds : "0" + seconds}
      </h1>
      <>
        <button onClick={updateTimer}>Update</button>
        <button onClick={pauseTimer}>Pause</button>
        <button onClick={onComplete}>Reset</button>
      </>
    </>
  );
};

export default Timer;
