import React from "react";

import "./styles.scss";
import { ComponentProps } from "./types";

const Timer: React.FC<ComponentProps> = ({
  settings,
  ticking,
  onComplete,
  updateTimer,
  pauseTimer,
  children,
}) => {
  const hours = settings.getHours();
  const minutes = settings.getMinutes();
  const seconds = settings.getSeconds();
  const disabledStart = !hours && !minutes && !seconds;
  return (
    <>
      <div className="timer">
        <p>{hours > 9 ? hours : "0" + hours}</p>
        <span>:</span>
        <p>{minutes > 9 ? minutes : "0" + minutes}</p>
        <span>:</span>
        <p>{seconds > 9 ? seconds : "0" + seconds}</p>
      </div>
      <div className="button-group">
        <button onClick={updateTimer}>Update</button>
        <button onClick={pauseTimer} disabled={disabledStart}>
          {ticking ? "Pause" : "Start"}
        </button>
        <button onClick={onComplete}>Reset</button>
      </div>
    </>
  );
};

export default Timer;
