import React, { useEffect, useState } from "react";

import { ComponentProps } from "./types";

import Layout from "../components/layout";
import Timer from "../components/timer";

const date = new Date("2020-01-01 00:00:05");

const TimerPage: React.FC<ComponentProps> = () => {
  const [timer, setTimer] = useState(date);

  useEffect(() => {
    setTimer(date);
  }, []);

  useEffect(() => {
    const tickTimer = setInterval(() => {
      setTimer((timer) => new Date(+timer - 1));
    }, 1000);

    // if (!timer.getHours()) clearInterval(tickTimer);
  }, [timer]);

  const updateTimer = (): void => {
    setTimer(date);
  };

  const pauseTimer = (): void => {};

  const resetTimer = (): void => {
    setTimer(new Date(new Date().setHours(0, 0, 0, 0)));
  };

  return (
    <Layout>
      <Timer settings={timer} updateTimer={updateTimer} onComplete={resetTimer}>
        {(hours: number, minutes: number, seconds: number) => (
          <>
            <span>{hours}</span>
            <span>{minutes}</span>
            <span>{seconds}</span>
          </>
        )}
      </Timer>
    </Layout>
  );
};

export default TimerPage;
