import React, { useEffect, useState } from "react";

import "./styles.scss";
import { ComponentProps } from "./types";

const initialTime = 10;

const Timer: React.FC<ComponentProps> = () => {
  const [state, setState] = useState(initialTime);

  useEffect(() => {
    setTimeout(() => {
      setState(state - 1);
    }, 1000);
    if (!state) clearTimeout();
  });

  const onTimerCompleted = (): void => {
    !state && console.log("Boom!");
  };

  onTimerCompleted();

  //hours->minutes-> seconds-> boom

  //00:00:00

  return (
    <>
      <h1 className="timer">{state}</h1>
    </>
  );
};

export default Timer;
