import React, { useEffect, useState } from "react";

import { ComponentProps } from "./types";

import Layout from "../components/layout";
import Timer from "../components/timer";

const date = new Date("July 16, 2018 05:35:05");

const initialHours = date.getHours();
const initialMinutes = date.getMinutes();
const initialSeconds = date.getSeconds();

const TimerPage: React.FC<ComponentProps> = () => {
  const [hours, setHours] = useState(initialHours);
  const [minutes, setMinutes] = useState(initialMinutes);
  const [seconds, setSeconds] = useState(initialSeconds);
  const [timer, setTimer] = useState(date);

  useEffect(() => {
    //Timers
    const hoursTimer = setInterval(() => {
      setHours(hours - 1);
    }, 3600000);

    const minutesTimer = setInterval(() => {
      setMinutes(minutes - 1);
    }, 60000);

    const secondsTimer = setInterval(() => {
      setSeconds(seconds - 1);
    }, 1000);

    // const mainTimer = setInterval(() => {
    //   setTimer(new Date(+timer - 1));
    // }, 1000);

    //Condition
    if (!hours) clearInterval(hoursTimer);
    if (!minutes) clearInterval(minutesTimer);
    if (!seconds) clearInterval(secondsTimer);
  }, [hours, minutes, seconds, timer]);

  const onTimerCompleted = (): void => {
    //!seconds && console.log("Boom!");
  };

  onTimerCompleted();

  return (
    <Layout>
      <Timer
        settings={timer}
        timer={timer}
        hours={hours}
        minutes={minutes}
        seconds={seconds}
        onComplete={() => {}}
      />
    </Layout>
  );
};

export default TimerPage;
